import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [messages, setMessages] = useState([
    { sender: 'Clio', text: 'üè∫ Hail, seeker of stories! Ask me any historical question.' }
  ]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);

  const API_URL = 'http://localhost:5000/api/chat';

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;
    const userMsg = { sender: 'You', text: input };
    setMessages(msgs => [...msgs, userMsg]);
    setLoading(true);
    setInput('');
    try {
      const res = await fetch(API_URL, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: input })
      });
      const data = await res.json();
      setMessages(msgs => [...msgs, { sender: 'Clio', text: data.reply }]);
    } catch (err) {
      setMessages(msgs => [...msgs, { sender: 'Clio', text: '‚ö†Ô∏è Sorry, I could not reach the server.' }]);
    }
    setLoading(false);
  };

  return (
    <div className="container py-5" style={{ maxWidth: 600 }}>
      <div className="card shadow">
        <div className="card-header bg-primary text-white">
          <h3>Clio - Historical Q&A Agent</h3>
        </div>
        <div className="card-body" style={{ height: 400, overflowY: 'auto', background: '#f8f9fa' }}>
          {messages.map((msg, idx) => (
            <div key={idx} className={`mb-2 ${msg.sender === 'You' ? 'text-end' : 'text-start'}`}>
              <span className={`badge ${msg.sender === 'You' ? 'bg-secondary' : 'bg-info text-dark'}`}>{msg.sender}</span>
              <div>{msg.text}</div>
            </div>
          ))}
          {loading && <div className="text-start"><span className="badge bg-info text-dark">Clio</span> <span>Typing...</span></div>}
        </div>
        <form className="card-footer d-flex" onSubmit={sendMessage}>
          <input
            className="form-control me-2"
            type="text"
            placeholder="Ask a historical question..."
            value={input}
            onChange={e => setInput(e.target.value)}
            disabled={loading}
          />
          <button className="btn btn-primary" type="submit" disabled={loading || !input.trim()}>
            Send
          </button>
        </form>
      </div>
    </div>
  );
}

export default App;
